import React from "react";
import styled from "styled-components";
import image from "../img/logo.png"
import HomeIcon from "@mui/icons-material/Home"; 

const Container = styled.div`
flex:1;
background-color:#202020;
height:200vh;
color:white;
font-size:14px
`;



const Wrapper = styled.div`
padding: 18px 26px

`

const Logo = styled.div`
  display:flex;
  align-items: center;
  gap: 5px;
  font-weight: bold;
  margin-bottom: 25px;
   
`;

const Img= styled.img`
height: 25px;
`

const Item = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  cursor: pointer;
  padding: 7.5px 0px;
  &:hover {
    background-color: ${({ theme }) => theme.soft};
  }
`;

const Hr = styled.hr`
  margin: 15px 0px;
  border: 0.5px solid ${({ theme }) => theme.soft};
`;

const Login = styled.div``;
const Button = styled.button`
  padding: 5px 15px;
  background-color: transparent;
  border: 1px solid #3ea6ff;
  color: #3ea6ff;
  border-radius: 3px;
  font-weight: 500;
  margin-top: 10px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
`;

const Title = styled.h2`
  font-size: 14px;
  font-weight: 500;
  color: #aaaaaa;
  margin-bottom: 20px;
`;




const Menu =()=>{
    return(
        <Container>
            <Wrapper>
                <Logo>
                    <Img src={image}/>
                    Stream.io
                </Logo>
                <Item>
                  <HomeIcon/>  
                  Home   
                </Item>

            </Wrapper>
        </Container>
    )
}


export default Menu
